# 导包import timeimport unittestfrom BeautifulReport import BeautifulReport# [测试用例-导包]from script.test_ihrm_login import TestIHRMLogin# 组织套件suite = unittest.TestSuite()suite.addTest(unittest.makeSuite(TestIHRMLogin))# 测试报告文件名 [更改文件名]file_name = 'IHRM登录测试用例-%s'% time.strftime("%Y%m%d%H%M%S")# 引入BeautifulReport# description - 用例名称# log_path - 保存路径BeautifulReport(suite).report(description='The IHEM Login Test Report', filename=file_name, log_path='./report')